import { Component, OnInit } from '@angular/core';
import { User } from '../user';
import { UserService } from '../user.service';

@Component({
  selector: 'app-actualizar-user',
  templateUrl: './actualizar-user.component.html',
  styleUrls: ['./actualizar-user.component.css']
})
export class ActualizarUserComponent implements OnInit {
  id:number;
  user:User = new User();
  constructor(private userService:UserService,private router:Router,private route:ActivatedRoute) { }


  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];
    this.adminService.obtenerAdminId(this.id).subscribe(dato =>{
      this.admin = dato;
    },error => console.log(error));

  }

  irAListaAdmin(){
   this.router.navigate(['/admins']);  }


onSubmit(){
  this.adminService.actualizarAdmin(this.id,this.admin).subscribe(dato=>{
    this.irAListaAdmin();
  },error=>console.log(error))
}

}
